// JavaScript Document

import React, { useState, useEffect } from 'react';


function FetchContent( { whichpage } ) {


	const [pages, setPages] = useState([]);

	useEffect(() => {
		fetch(`https://dev.justinblayney.com/wp-json/wp/v2/pages/{whichpage}`)
		.then(res => res.json())

		.then(json =>
			 setPages(json)
			  )

		.then(json => console.log(json))
		 });


	return (
	<>	
		{Object.keys(pages).map((ky, idx) => (

			<div id={whichpage} key={idx}>
				<h1>{pages.title.rendered}</h1>
				<div dangerouslySetInnerHTML={ {__html: pages.content.rendered} } />
				{}
			</div>

		))}

	</>

	);


}


function Page( { pageid } ) {
  return (
    <div className="container-fluid">
		<FetchContent whichpage={ pageid } />
	 </div>
  );
}

export default Page;
