import React, { Component, useState, useEffect } from 'react';
import {
	Link
} from 'react-router-dom';
function Nav() {
  const [menus, setMenus] = useState([]);

  useEffect(() => {
    fetch("main-menu.json", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      }
    })
      .then((res) => res.json())

      .then((json) => {
        console.log(json);
        setMenus(json.items);
      });
  }, []);



 return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <button
        className="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarNavDropdown"
        aria-controls="navbarNavDropdown"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>

      <div className="collapse navbar-collapse" id="navbarNavDropdown">
        <ul className="navbar-nav">
          {(menus || []).map((item, ix) => (
            <li key={ix} className="nav-item active">
              <Link
                className="nav-link"
                to={{
                  pathname: item.slug,
                  state: {
                    pageId: item.slug
                  }
                }}
              >
                {item.title}
              </Link>

              {(item.child_items || []).map((childItem, ix) => (
                <div key={ix} className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                  <Link
                    className="dropdown-item"
                    to={{
                      pathname: childItem.slug,
                      state: {
                        pageId: childItem.slug
                      }
                    }}
                  >
                    {childItem.title}
                  </Link>
                </div>
              ))}
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}





class Header extends Component {

render() {
	return (

    <header>
	  <div className="container-fluid">
	  	<div className="row">
			 <div className="col-12">
	  			<div className="logo">Logo</div>
				  <Nav />


			</div> 
		  </div>
		</div>
    </header>
  );
}}

export default Header;
