import React, { Component, useState, useEffect } from 'react';
import {
	BrowserRouter as Router,
	Route,
	Link
} from 'react-router-dom';


import './Assets/css/default.min.css';
import Header from './Components/HeaderComponent/header';
import Footer from './Components/FooterComponent/footer';
import HomePage from './Components/Pages/homePage';
import ArtPage from './Components/Pages/artPage';
import Page from './Components/Pages/Page';
import ContactPage from './Components/Pages/contactPage';






function MyRoutes() {

    const [menus, setMenus] = useState([]);

    useEffect(() => {
        fetch("https://dev.justinblayney.com/wp-json/menus/v1/menus/main-menu")
        .then(res => res.json())

        .then(json =>{
             setMenus(json.items)}
              )

        .then(json => console.log(json))
         });


    return (

		<>

			{Object.keys(menus).map((ky, idx)=> (
				<Route exact path={menus[ky].slug} component={Page, menus[ky].ID }   id={idx} key={idx} />

			))}


		</>

    );
}




class App extends Component {
	render() {
		return(

	 <Router> 
		<div className="App">
			<Header />		

				<Route exact path='/' component={HomePage} />
				<MyRoutes />


			<Footer />

		 </div>
	  </Router> 

		);	
	}
}

export default App;
