import React, { useState, useEffect } from 'react';
import {
  useLocation
} from "react-router-dom";
import PropTypes from 'prop-types';


function FetchContent( { pageId } ) {


	const [pages, setPages] = useState([]);

	useEffect(() => {
		fetch(`https://dev.justinblayney.com/wp-json/wp/v2/pages/{pageId}`)
		.then(res => res.json())

		.then(json =>
			 setPosts(json)
			  )

		.then(json => console.log(json))
		 });


	return (
	<ul>
		{posts.map(post => (
         <li key={post.id}>{post.title.rendered}</li>
        ))}

		</ul>

	);

	useEffect(() => {
		fetch(`https://dev.justinblayney.com/wp-json/wp/v2/pages/{pageId}`)
		.then(res => res.json())

		.then(json =>
			 setPages(json)
			  )

		.then(json => console.log(json))
		 });


	return (
	<>	
		{Object.keys(pages).map((ky, idx) => (

			<div id={idx} key={idx}>
				<h1>{pages[ky].title}</h1>
				<div dangerouslySetInnerHTML={ {__html: pages[ky].content} } />
				{}
			</div>

		))}

	</>

	);


}


function Page() {
  const pageId = 2414;


  return (
    <div className="container-fluid">

	  <FetchContent  pageId={pageId} />

	  <h3>Requested page ID: {pageId}</h3>


	 </div>
  );
}

export default Page;
