import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

function FetchContent({ pageId }) {
const [foundItem, setFoundItem] = useState();
  useEffect(() => {
    fetch("pages.json", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      }
    })
      .then((res) => res.json())
      .then((json) => {
        const found = json.find(({id}) => (id === pageId));
        setFoundItem(found);
      });
  }, [pageId]);

  if (!foundItem) return <div />;
  return (
    <>
      <h1 key={foundItem.id}>
        {foundItem.title && foundItem.title.rendered ? foundItem.title.rendered: ''}
      </h1>
	   <div contentEditable='true' dangerouslySetInnerHTML={{ __html: "Hello" }}></div>
	  {foundItem.content && foundItem.content.rendered ? foundItem.content.rendered: ''}
    </>
  );
}

function Page() {
const location = useLocation();
const [pageId, setPageId] = useState(null);
useEffect(()=> {
  if(location.state){
    setPageId(parseInt(location.state.pageId, 10));
  }
}, [location]);
  return (
    <div className="container-fluid">
      <FetchContent pageId={pageId} />
    </div>
  );
}

export default Page;
