// JavaScript Document

import React, { useState, useEffect } from 'react';
import {
  useLocation
} from "react-router-dom";


function FetchContent( { pageId } ) {


	const [pages, setPages] = useState([]);

	useEffect(() => {

		fetch(`https://dev.justinblayney.com/wp-json/wp/v2/pages/{pageId}?_limit=1`)
		.then(res => res.json())

		.then(json =>
			 setPages(json)
			  )

		.then(json => console.log(json))
		 });


	return (
	<>	
		{Object.keys(pages).map((ky, idx) => (

			<div id={pageId} key={idx}>
				<h1>{`/${pages[ky].slug}`}xx</h1>
				<div dangerouslySetInnerHTML={ {__html: pages[ky].content.rendered} } />
				{}
			</div>

		))}

	</>

	);


}


function Page() {
  const { state:  { pageId }  } = useLocation();


  return (
    <div className="container-fluid">

	  <FetchContent  pageId={pageId} />

	  <h3>Requested page ID: {pageId}</h3>


	 </div>
  );
}

export default Page;
